class Histogram:
    def __init__(self, minval=0, maxval=100, resolution=1000):
        self.minval = minval
        self.maxval = maxval
        self.bins = [0] * resolution
        self.total = 0

    def getbin(self, v):
        if v < self.minval:
            return 0
        if v > self.maxval:
            return self.bins[-1]
        bin = int((v-self.minval) / (self.maxval - self.minval) * len(self.bins))
        if bin >= len(self.bins):
            return len(self.bins) - 1
        return bin

    def learn(self, v):
        """
        add tnew data
        """
        bin = self.getbin(v)
        self.bins[bin] += 1
        self.total += 1

    # minimum value -> 0, maximum value -> 1
    def percentile(self, v):
        if self.total == 0:
            return 0
        # number of values less than the value
        cnt = 0
        bin = self.getbin(v)
        for i in range(bin):
            cnt += self.bins[i]
        return cnt / self.total * 100


